import React, { useRef } from "react";
import {
  Navbar,
  Nav,
  Container,
  Button,
  Modal,
  Form,
  Card,
} from "react-bootstrap";
import SignaturePad from "react-signature-canvas";
import "./landingPage.css";
import { accountsData } from "../modules/accountData";
import { useFormik } from "formik";

export default function CreateAccount() {
  let sigPad = useRef({});
  let signature = "";

  function clear() {
    sigPad.current.clear();
  }

  function save() {
    sigPad.current.save();
  }

  function show() {
    sigPad.current.show();
  }

  const initialValues = {
    phoneNumber: "",
    name: "",
    email: "",
    username: "",
    password: "",
    signature: "",
  };

  const onSubmit = (values) => {
    let account = { ...values };
    let accountJSON = JSON.stringify(account);
    accountsData.push(account);
  };

  const formik = useFormik({
    initialValues,
    onSubmit,
  });

  return (
    <div className="formPage">
      <Card className="form">
        <Card.Header>
          <h2>Create an Account</h2>
        </Card.Header>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Phone Number:</Form.Label>
            <Form.Control
              type="text"
              placeholder="0801 124 5678"
              name="phoneNumber"
            />
          </Form.Group>

          <Form.Group className="mb-4">
            <Form.Label>Full Name:</Form.Label>
            <Form.Control type="text" placeholder="John Doe" name="fullName" />
          </Form.Group>

          <Form.Group className="mb-4">
            <Form.Label>Email:</Form.Label>
            <Form.Control
              type="email"
              placeholder="john.doe@hotmail.com"
              name="email"
            />
          </Form.Group>
        </Form>

        <div className="signaturePad" style={{ border: " solid black 1px" }}>
          <SignaturePad
            className="m-signature-pad"
            ref={sigPad}
            penColor="black"
          />
        </div>
        <div className="signatureButtons" style={{ padding: "2px" }}>
          <Button variant="dark">
            <i class="fas fa-arrow-left"></i>Back
          </Button>
          <Button variant="warning" onClick={clear}>
            {" "}
            Clear Signature
          </Button>
          <Button onClick={save} variant="info">
            <i class="fas fa-sign-in-alt"></i>Check in
          </Button>
        </div>
      </Card>
    </div>
  );
}
