import React, { useRef } from "react";
import { Navbar, Nav, Container, Button, Modal, Form } from "react-bootstrap";
import SignaturePad from "react-signature-canvas";

export default function CreateAccount() {
  let sigPad = useRef({});
  let signature = "";

  function clear() {
    sigPad.current.clear();
  }

  function save() {
    sigPad.current.save();
  }

  function show() {
    sigPad.current.show();
  }

  return (
    <div style={{ width: "65%" }}>
      <Form>
        <Form.Group className="mb-4">
          <Form.Label>Phone Number:</Form.Label>
          <Form.Control
            type="text"
            placeholder="0801 124 5678"
            name="phoneNumber"
          />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Full Name:</Form.Label>
          <Form.Control type="text" placeholder="John Doe" name="fullName" />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Email:</Form.Label>
          <Form.Control
            type="email"
            placeholder="john.doe@hotmail.com"
            name="email"
          />
        </Form.Group>
      </Form>

      <SignaturePad className="m-signature-pad" ref={sigPad} penColor="black" />
      <div className="signatureButtons">
        <Button variant="dark">
          <i class="fas fa-arrow-left"></i>Back
        </Button>
        <Button variant="warning" onClick={clear}>
          {" "}
          Clear Signature
        </Button>
        <Button onClick={save} variant="info">
          <i class="fas fa-sign-in-alt"></i>Check in
        </Button>
      </div>
    </div>
  );
}
